			// expand/contract the editor as the content changes.
			var html = "<div id='dijitEditorBody' dir='" + (this.isTextDirLeftToRight()? "ltr" : "rtl") + "'></div>";

			var font = [ _cs.fontWeight, _cs.fontSize, _cs.fontFamily ].join(" ");

